import{_ as r,e as t}from"./app.d5447752.js";const s={},n=t('<h1 id="\u597D\u611F\u673A\u5236" tabindex="-1"><a class="header-anchor" href="#\u597D\u611F\u673A\u5236" aria-hidden="true">#</a> \u597D\u611F\u673A\u5236</h1><p>\u8FD9\u91CC\u5C06\u4ECB\u7ECD V4.6 \u7248\u672C\u7684\u5168\u65B0\u597D\u611F\u5EA6\u673A\u5236\uFF0C\u76F8\u8F83\u4E8E\u4EE5\u524D\u505A\u4E86\u5927\u89C4\u6A21\u7684\u6539\u52A8\u3002</p><p>V4.6 \u5F15\u5165\u4E86\u989D\u5916\u56DB\u4E2A\u53D8\u91CF\uFF0C\u5206\u522B\u662F<strong>\u6821\u51C6\u503C\u3001\u6821\u51C6\u4E0A\u9650\u3001\u4EB2\u5BC6\u5EA6\u3001\u4EB2\u548C\u5EA6\u3001\u8FB9\u9645\u62B5\u6297</strong>\u3002</p><h3 id="\u6821\u51C6\u4E0A\u9650" tabindex="-1"><a class="header-anchor" href="#\u6821\u51C6\u4E0A\u9650" aria-hidden="true">#</a> \u6821\u51C6\u4E0A\u9650</h3><p>\u8FD9\u662F\u4E00\u4E2A\u5168\u5C40\u6570\u636E\uFF0C\u5982\u679C<strong>\u6821\u51C6\u503C</strong>\u8FBE\u5230\u8BE5\u4E0A\u9650\uFF0C\u5FC5\u987B\u8FDB\u884C\u4E00\u6B21<strong>\u6821\u51C6</strong>\u624D\u80FD\u7EE7\u7EED\u6B63\u5E38\u4EA4\u4E92\u3002\u521D\u59CB\u503C\u4E3A 12</p><h3 id="\u6821\u51C6\u503C" tabindex="-1"><a class="header-anchor" href="#\u6821\u51C6\u503C" aria-hidden="true">#</a> \u6821\u51C6\u503C</h3><p>\u8FD9\u662F\u4E00\u4E2A\u5168\u5C40\u6570\u636E\uFF0C\u6BCF\u8FDB\u884C\u4E00\u6B21\u4EA4\u4E92\u589E\u52A0 1 \u6821\u51C6\u503C\uFF0C\u4E14\u8FDB\u884C\u4E00\u6B21\u4EA4\u4E92\u68C0\u5B9A\uFF0C1/10 \u7684\u6982\u7387\u5224\u5B9A\u4EA4\u4E92\u5931\u8D25\uFF0C\u8FD9\u5C06\u589E\u52A0 2 \u70B9<strong>\u6821\u51C6\u4E0A\u9650</strong>\u3002</p><p>\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5982\u679C\u6CA1\u6709\u8FBE\u5230\u76F8\u5E94<strong>\u52A8\u4F5C\u7C7B\u4EA4\u4E92</strong>\u6240\u9700\u6700\u4F4E\u597D\u611F\u5EA6\uFF0C\u5C06\u4F1A\u589E\u52A0 3 \u70B9<strong>\u6821\u51C6\u4E0A\u9650</strong>(\u548C\u4EA4\u4E92\u5931\u8D25\u5224\u5B9A\u76F8\u4E92\u72EC\u7ACB)</p><p>\u5F53\u6821\u51C6\u503C\u8FBE\u5230\u4E0A\u9650\u65F6\uFF0C\u9700\u8981\u8FDB\u884C\u6821\u51C6\u624D\u53EF\u4EE5\u7EE7\u7EED\u4EA4\u4E92\uFF0C<strong>\u6307\u4EE4\u4E3A \u8309\u8389\u6821\u51C6</strong></p><div class="custom-container warning"><p class="custom-container-title">\u6CE8\u610F</p><p>\u6240\u6709\u7528\u6237\u90FD\u80FD\u5BF9\u6821\u51C6\u503C\u548C\u6821\u51C6\u4E0A\u9650\u4EA7\u751F\u5F71\u54CD\u3002</p></div><h3 id="\u4EB2\u5BC6\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u4EB2\u5BC6\u5EA6" aria-hidden="true">#</a> \u4EB2\u5BC6\u5EA6</h3><p>\u9664\u597D\u611F\u5EA6\u5916\u8861\u91CF\u548C\u8309\u8389\u4EB2\u5BC6\u7A0B\u5EA6\u7684\u53E6\u4E00\u4E2A\u6307\u6807\uFF0C\u7531<strong>\u597D\u611F\u5EA6</strong>\u548C<strong>\u901A\u8FC7\u7684\u5267\u60C5\u7AE0\u8282</strong>\u5171\u540C\u51B3\u5B9A\uFF0C\u5C06\u7528\u4E8E\u540E\u7EED\u9053\u5177\u89E3\u9501\u7B49\u65B9\u9762\uFF0C\u4E00\u4E9B\u7269\u54C1\u53EA\u6709\u8FBE\u5230\u76F8\u5E94\u4EB2\u5BC6\u5EA6\u624D\u80FD\u8D2D\u4E70\u3002</p><p>\u597D\u611F\u8FBE\u5230 1000 \u4E14\u901A\u8FC7\u5E8F\u7AE0\uFF1A\u4EB2\u5BC6\u5EA6 1\u3002\u597D\u611F\u8FBE\u5230 2000 \u4E14\u901A\u8FC7\u7B2C\u4E00\u7AE0\uFF1A\u4EB2\u5BC6\u5EA6 2\u3002</p><p>\u597D\u611F\u8FBE\u5230 3000 \u4E14\u901A\u8FC7\u7B2C\u4E8C\u7AE0\uFF1A\u4EB2\u5BC6\u5EA6 3</p><p>\u4EE5\u6B64\u7C7B\u63A8\u3002</p><h3 id="\u4EB2\u548C\u5EA6" tabindex="-1"><a class="header-anchor" href="#\u4EB2\u548C\u5EA6" aria-hidden="true">#</a> \u4EB2\u548C\u5EA6</h3><p>\u7528\u4E8E\u6307\u793A\u548C\u8309\u8389\u4EA4\u4E92\u7684&quot;\u6548\u7387&quot;\uFF0C\u56E0\u4E3A\u771F\u5B9E\u60C5\u51B5\u4E0B\uFF0C\u5728\u4E0D\u540C\u65F6\u5019\u505A\u4E0D\u540C\u4E8B\u4EA7\u751F\u7684\u6548\u679C\u4EA6\u4E0D\u540C\uFF0C\u6240\u4EE5\u8FD9\u9879\u6307\u6807\u663E\u5F97\u4E3E\u8DB3\u8F7B\u91CD\uFF0C\u901A\u4FD7\u6765\u8BB2\u5C31\u662F\uFF0C<s>\u8981\u8D81\u70ED\uFF01</s></p><p>\u4E00\u6B21\u6210\u529F\u7684\u4EA4\u4E92\u5C06\u5F97\u5230\u4E00\u5B9A\u4EB2\u548C\u5EA6;\u4E00\u6B21\u5931\u8D25\u7684\u4EA4\u4E92\u5C06\u5931\u53BB\u4E00\u5B9A\u4EB2\u548C\u5EA6\uFF0C\u82E5\u672A\u8FBE\u5230\u597D\u611F\u800C\u6267\u884C\u4E86\u76F8\u5173\u64CD\u4F5C\u4E5F\u5C06\u964D\u4F4E\u4EB2\u548C\u5EA6\uFF0C\u4E24\u8005\u4E92\u76F8\u72EC\u7ACB\u3002</p><p>\u8303\u56F4\u4E3A 0~100\u3002</p><div class="custom-container warning"><p class="custom-container-title">\u6CE8\u610F</p><p>V4.6 \u4E2D\uFF0C\u597D\u611F\u5EA6<strong>\u9022\u5343</strong>\u9700\u8981\u8FDB\u884C\u4EB2\u548C\u5EA6\u68C0\u9A8C\uFF0C\u53EA\u6709\u8FBE\u5230<strong>100 \u4EB2\u548C\u5EA6</strong>\u624D\u80FD\u7A81\u7834\u4E0A\u9650\uFF0C\u7A81\u7834\u4E0A\u9650\u540E<strong>\u4EB2\u548C\u5EA6\u6E05 0</strong>\u3002</p></div><div class="custom-container danger"><p class="custom-container-title">\u8981\u5C0F\u5FC3</p><p>\u5982\u679C\u7A81\u7834\u540E\u91CD\u65B0\u6389\u4E0B\u5343\uFF0C\u4EB2\u548C\u5EA6\u5C06\u4E0D\u4F1A\u56DE\u590D\uFF01\u8BF7\u8003\u8651\u4E00\u5B9A\u7B56\u7565\uFF0C\u5E76\u6CE8\u610F\u914D\u5408\u9053\u5177\u548C\u4EA4\u4E92\u65F6\u673A\u3002</p></div><h3 id="\u8FB9\u9645\u62B5\u6297" tabindex="-1"><a class="header-anchor" href="#\u8FB9\u9645\u62B5\u6297" aria-hidden="true">#</a> \u8FB9\u9645\u62B5\u6297</h3><p>\u4E5F\u8BB8\u4F60\u6709\u8FD9\u79CD\u611F\u89C9\uFF0C<s>\u5F53\u7136\u6CA1\u6709\u5C31\u7406\u89E3\u7406\u89E3</s>,\u5728\u70ED\u604B\u671F\u95F4\uFF0C\u4EBA\u4EEC\u76F8\u5BF9\u5BB9\u6613\u88AB\u5BF9\u65B9\u6253\u52A8\uFF0C\u800C\u65F6\u95F4\u4E00\u957F\uFF0C\u53CD\u800C\u540C\u4E00\u4EF6\u4E8B\u4EA7\u751F\u7684\u6548\u679C\u5C06\u5927\u6253\u6298\u6263\u3002\u5982\u4F55\u5E94\u5BF9\u65F6\u95F4\u5BF9\u4F60\u4EEC\u7684\u78E8\u635F\u4E5F\u8BB8\u662F\u9700\u8981\u8003\u8651\u7684\u95EE\u9898\u3002</p><p>\u800C\u8FD9\u9879\u6570\u636E\u5C31\u662F\u7528\u6765\u6307\u793A\u8BE5\u5185\u5BB9\u7684\uFF0C\u548C\u597D\u611F\u5EA6\u6210\u6B63\u76F8\u5173\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u597D\u611F\u5EA6\u8D8A\u9AD8\uFF0C\u8FB9\u9645\u62B5\u6297\u8D8A\u9AD8\u3002\u5F53\u7136\u4E86\uFF0C\u8FD9\u66F4\u591A\u7528\u4E8E\u540E\u53F0\u7A0B\u5E8F\u8BA1\u7B97\uFF0C\u5E76\u6CA1\u6709\u5355\u72EC\u663E\u793A\u7684\u5FC5\u8981\u3002</p><h3 id="\u90A3\u4E48-\u5B83\u4EEC\u5177\u4F53\u6709\u4EC0\u4E48\u7528" tabindex="-1"><a class="header-anchor" href="#\u90A3\u4E48-\u5B83\u4EEC\u5177\u4F53\u6709\u4EC0\u4E48\u7528" aria-hidden="true">#</a> \u90A3\u4E48\uFF0C\u5B83\u4EEC\u5177\u4F53\u6709\u4EC0\u4E48\u7528\uFF1F</h3><p>\u5728\u4E4B\u524D\u7684\u7248\u672C\u4E2D\uFF0C\u6267\u884C\u67D0\u4EA4\u4E92\u7684\u597D\u611F\u9608\u503C\u662F\u4E0D\u516C\u5F00\u7684\uFF0C\u800C\u5728 V4.6 \u4E2D\uFF0C\u6267\u884C\u67D0\u4EA4\u4E92\u6240\u9700\u8981\u7684\u597D\u611F\u4E0D\u4EC5\u4E0D\u516C\u5F00\uFF0C\u4E14\u5C06\u4F1A<strong>\u5728\u67D0\u4E00\u4E2A\u8303\u56F4\u4E0A\u4E0B\u6CE2\u52A8</strong></p><p>\u8BE5\u8303\u56F4\u7684\u4E0B\u9650\u4E0E<strong>\u4EB2\u548C\u5EA6</strong>\u6B63\u76F8\u5173\uFF08\u4EB2\u548C\u5EA6\u8D8A\u5927\uFF0C\u4E0B\u9650\u8D8A\u4F4E\uFF09,\u4E0A\u9650\u4E0E<strong>\u8FB9\u9645\u62B5\u6297</strong>\u548C<strong>\u6821\u51C6\u503C</strong>\u6B63\u76F8\u5173\uFF08\u8FB9\u9645\u62B5\u6297\u548C\u6821\u51C6\u503C\u8D8A\u5927\uFF0C\u4E0A\u9650\u8D8A\u9AD8\uFF09</p><p>e.g \u5047\u8BBE\u539F\u4EA4\u4E92\u9700\u8981\u8FBE\u5230 1000 \u597D\u611F\u624D\u4E0D\u4F1A\u964D\u4F4E\u597D\u611F\uFF0C\u73B0\u7531\u4EB2\u548C\u5EA6\u51B3\u5B9A\u4E86\u4E00\u4E2A\u4E0B\u9650 a\uFF0C\u7531\u8FB9\u9645\u62B5\u6297\u548C\u6821\u51C6\u503C\u51B3\u5B9A\u4E86\u4E0A\u9650 b\uFF0C\u90A3\u4E48\u7A0B\u5E8F\u5728[a,b]\u4E2D\u9009\u53D6\u4E00\u4E2A\u503C c\uFF0C\u5F53\u597D\u611F\u8FBE\u5230 c \u5C31\u4E0D\u4F1A\u964D\u4F4E\u597D\u611F\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u8FD9\u79CD\u89C4\u5219\u8FD8\u5C06\u9002\u7528\u4E8E<strong>\u597D\u611F\u53D8\u5316</strong>\u4E2D\u3002</p><p>\u5728 V4.6 \u7248\u672C\u4E2D\uFF0C\u6267\u884C\u67D0\u4E00\u9879\u4EA4\u4E92\u589E\u52A0/\u51CF\u5C11\u7684\u597D\u611F\u5C06\u4E0D\u518D\u56FA\u5B9A\u6216\u8005\u7B80\u5355\u7684\u5728\u8303\u56F4\u5185\u968F\u673A\uFF0C\u6211\u4EEC\u5C06\u6309\u7167\u4E4B\u524D\u7684\u65B9\u6CD5\u5F97\u5230\u4E00\u4E2A\u9884\u4F30\u503C v\uFF0C\u7136\u540E\u901A\u8FC7\u6821\u51C6\u503C\u3001\u8FB9\u9645\u62B5\u6297\u3001\u4EB2\u548C\u5EA6\u6765\u5BF9 v \u8FDB\u884C\u4FEE\u6B63\uFF0C\u5F97\u5230\u4E86\u6700\u540E\u7684\u7ED3\u679C r \u624D\u662F\u6700\u7EC8\u7684\u597D\u611F\u53D8\u5316\u503C\u3002</p><p>\u76F8\u4FE1\u4F60\u5DF2\u7ECF\u77E5\u9053\u4E86\uFF0Cr \u548C\u6821\u51C6\u503C\u3001\u8FB9\u9645\u62B5\u6297\u4E3A\u8D1F\u76F8\u5173\uFF0C\u548C\u4EB2\u548C\u5EA6\u4E3A\u6B63\u76F8\u5173\u3002</p><div class="custom-container info"><p class="custom-container-title">\u7B80\u800C\u8A00\u4E4B</p><p>\u4F60\u53EF\u4EE5\u901A\u4FD7\u5730\u7406\u89E3\u4E3A\uFF0C<strong>\u4EB2\u548C\u5EA6\u8D8A\u9AD8\u8D8A\u597D\uFF0C\u8FB9\u9645\u62B5\u6297\u548C\u6821\u51C6\u503C\u8D8A\u4F4E\u8D8A\u597D</strong>\uFF0C\u5982\u679C\u4F60\u7684\u4EB2\u548C\u5EA6\u5F88\u4F4E\uFF0C\u800C\u8FB9\u9645\u62B5\u6297\u548C\u6821\u51C6\u503C\u53C8\u9AD8\uFF0C\u4F60\u5F97\u5230\u7684\u597D\u611F\u5C06\u5165\u4E0D\u6577\u51FA \xD7\u3002</p></div><p>\u53E6\u5916\uFF0C\u6BCF\u4E00\u4E2A\u65F6\u949F\u5468\u671F\uFF08\u4ECE\u4E00\u6B21\u6821\u51C6\u5230\u4E0B\u4E00\u6B21\u6821\u51C6\u4E4B\u95F4\uFF09\uFF0C\u5C06\u4F1A\u8BB0\u5F55\u4E0B\u5404\u7528\u6237\u6267\u884C\u4EA4\u4E92\u4EA7\u751F\u7684<strong>\u6700\u540E\u4E00\u6B21\u597D\u611F\u53D8\u5316</strong>\u3002</p><p>\u5F53\u6267\u884C\u6821\u51C6\u65F6\uFF0C\u5C06\u8FDB\u884C\u4E00\u6B21\u6821\u51C6\u5224\u5B9A\uFF0C\u6709 1/5 \u7684\u6982\u7387\u5206\u522B\u4E3A\u5B8C\u7F8E\u3001\u5931\u8D25\u6821\u51C6\uFF0C3/5 \u6982\u7387\u4E3A\u666E\u901A\u6210\u529F\u6821\u51C6\u3002</p><p>\u666E\u901A\u6210\u529F\u5C06\u4E0D\u4F1A\u6709\u4EFB\u4F55\u4E8B\u53D1\u751F\uFF0C\u800C\u5B8C\u7F8E\u6821\u51C6\u5C06\u4F1A\u5C06\u8BE5\u5468\u671F\u5185\u6240\u6709\u7528\u6237\u6240\u4EA7\u751F\u7684<strong>\u6700\u540E\u4E00\u6B21</strong>\u597D\u611F<strong>\u6B63\u5411\u53D8\u5316</strong>\u91CD\u65B0\u6267\u884C;\u5931\u8D25\u7684\u6821\u51C6\u5C06\u8BE5\u5468\u671F\u5185\u6240\u6709\u7528\u6237\u6240\u4EA7\u751F\u7684\u6700\u540E\u4E00\u6B21\u597D\u611F\u53D8\u5316<strong>\u53D6\u7EDD\u5BF9\u503C\u540E\u76F8\u51CF</strong>\u3002</p><p>\u82E5\u6821\u51C6\u4E0A\u9650<strong>\u8D85\u51FA\u4E86 12</strong>\u7684\u90E8\u5206\u5C06\u4F5C\u4E3A<strong>\u51CF\u76CA</strong>\u52A0\u5165\u5931\u8D25\u6821\u51C6\u7684\u8BA1\u7B97\u3002</p><p>\u5F88\u96BE\u7406\u89E3\uFF1F\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u5047\u8BBE\u7528\u6237 a \u5728\u672C\u5468\u671F\u4E2D\u8FDB\u884C\u4E86\u4EA4\u4E92\uFF0C\u597D\u611F\u53D8\u5316\u4E3A +10 +20\uFF0C\u7528\u6237 b \u540C\u7406\uFF0C\u597D\u611F\u53D8\u5316\u4E3A+20 -10\uFF0C\u5F53\u524D\u6821\u51C6\u4E0A\u9650\u4E3A 15,15-12=3</p><blockquote><p>\u5B8C\u7F8E\u6821\u51C6\u7684\u60C5\u51B5:\u7528\u6237 a \u518D\u6B21\u5F97\u5230\u4E86 20 \u597D\u611F\uFF0C\u7528\u6237 b \u597D\u611F\u4E0D\u53D8\uFF0C\u56E0\u4E3A\u5B8C\u7F8E\u6821\u51C6<strong>\u53EA\u8003\u8651\u6B63\u5411\u53D8\u5316</strong>\uFF0C\u539F\u5148\u7684-10 \u88AB\u4E22\u5F03\u4E86\u3002</p><p>\u5931\u8D25\u6821\u51C6\u7684\u60C5\u51B5\uFF1A\u7528\u6237 a \u5931\u53BB\u4E86 20+3 \u597D\u611F\uFF0C\u7528\u6237 b \u5931\u53BB 10+3 \u597D\u611F\u3002</p></blockquote><div class="custom-container danger"><p class="custom-container-title">\u5C0F\u5FC3</p><p>\u6821\u51C6\u4F1A\u5BF9\u8BE5\u5468\u671F\u5185\u6240\u6709\u7528\u6237\u4EA7\u751F\u5F71\u54CD\u3002 \u7269\u54C1\u4EA7\u751F\u7684\u597D\u611F\u53D8\u5316\u4E5F\u9002\u7528\u4E8E\u4E0A\u8FF0\u6240\u6709\u89C4\u5219\u4E14\u4F1A\u88AB\u6821\u51C6\u6240\u8BB0\u5F55\uFF01</p></div><p>\u5982\u6709\u7591\u95EE\u8BF7\u53CA\u65F6\u8054\u7CFB\u53CD\u9988\u54E6~</p>',40);function a(o,p){return n}var i=r(s,[["render",a]]);export{i as default};
